/* Lightweight QR stub: renders an IMG via public API */
!function(w){
  function QRCode(el, opt){
    opt = opt || {};
    var width = opt.width || 128;
    var height = opt.height || 128;
    var text = (opt.text || '').toString();
    var img = document.createElement('img');
    img.width = width;
    img.height = height;
    img.alt = 'QR';
    img.referrerPolicy = 'no-referrer';
    img.src = 'https://api.qrserver.com/v1/create-qr-code/?size='+width+'x'+height+'&data='+encodeURIComponent(text);
    el.innerHTML = '';
    el.appendChild(img);
  }
  QRCode.CorrectLevel = { L:1, M:0, Q:3, H:2 };
  w.QRCode = QRCode;
}(window);